@ExtendWith(org.mockito.junit.jupiter.MockitoExtension.class)
public class PDFComparatorTest {

private PDFComparator pdfComparator;
private PDFPageComparator pageComparator;
private InputStream pdf1InputStream;
private InputStream pdf2InputStream;
private OutputStream outputStream;

@BeforeEach
public void setUp() {
pdfComparator = new PDFComparator();
pageComparator = mock(PDFPageComparator.class);
pdfComparator.setPageComparator(pageComparator);
pdf1InputStream = new ByteArrayInputStream("PDF1".getBytes());
pdf2InputStream = new ByteArrayInputStream("PDF2".getBytes());
outputStream = new ByteArrayOutputStream();
}

@AfterEach
public void tearDown() {
try {
pdf1InputStream.close();
pdf2InputStream.close();
outputStream.close();
} catch (Exception e) {
// Ignore
}
}

@Test
public void testComparePDFs_NormalCase_Success() {
pdfComparator.comparePDFs(pdf1InputStream, pdf2InputStream, outputStream, false);
assertNotNull(outputStream.toByteArray());
}

@Test
public void testComparePDFs_NullInputStream_ThrowsIOException() {
assertThrows(IOException.class, () -> pdfComparator.comparePDFs(null, pdf2InputStream, outputStream, false));
assertThrows(IOException.class, () -> pdfComparator.comparePDFs(pdf1InputStream, null, outputStream, false));
}

