public void testComparePDFs_SinglePage_MergesPages() throws Exception {
when(pageComparator.comparePage(any(PdfReader.class), any(PdfReader.class), anyInt(), any(PdfContentByte.class), anyFloat(), anyFloat(), anyFloat(), anyFloat(), anyBoolean()))
.thenReturn(null);

pdfComparator.comparePDFs(pdf1InputStream, pdf2InputStream, outputStream, false);

assertNotNull(outputStreamMock.toByteArray());
}

