package com.example.pdfcompare.util;

import com.example.pdfcompare.model.ImageChunk;
import com.example.pdfcompare.util.ImageComparator;
import com.example.pdfcompare.util.PDFHighlighter;
import com.itextpdf.text.BaseColor;
import com.itextpdf.text.pdf.PdfContentByte;
import java.util.List;
import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.junit.jupiter.MockitoExtension;

@ExtendWith(MockitoExtension.class)
public class ImageComparatorTest {

@Mock
private PDFHighlighter pdfHighlighterMock;

@InjectMocks
private ImageComparator imageComparator;

private PdfContentByte pdfContentByteMock;

@BeforeEach
public void setUp() {
pdfContentByteMock = mock(PdfContentByte.class);
}

@AfterEach
public void tearDown() {
pdfContentByteMock = null;
}

@Test
public void testCompareImages_normalCase_isMultiple_true() {
ImageChunk imageChunk1 = new ImageChunk("hash1", new Rectangle(0, 0, 100, 100));
ImageChunk imageChunk2 = new ImageChunk("hash2", new Rectangle(0, 0, 100, 100));
List<ImageChunk> images1 = List.of(imageChunk1);
List<ImageChunk> images2 = List.of(imageChunk2);

imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, true);

verify(pdfHighlighterMock, times(1)).highlightChunk(any(), any(), any(), eq(BaseColor.RED), eq(0));
verify(pdfHighlighterMock, times(1)).highlightChunk(any(), any(), any(), eq(BaseColor.GREEN), eq(100));
}

@Test
public void testCompareImages_normalCase_isMultiple_false() {
ImageChunk imageChunk1 = new ImageChunk("hash1", new Rectangle(0, 0, 100, 100));
ImageChunk imageChunk2 = new ImageChunk("hash2", new Rectangle(0, 0, 100, 100));
List<ImageChunk> images1 = List.of(imageChunk1);
List<ImageChunk> images2 = List.of(imageChunk2);

imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, false);

verify(pdfHighlighterMock, times(1)).highlightChunk(any(), any(), any(), eq(BaseColor.GREEN), eq(0));
}

@Test
public void testCompareImages_deleteCase_isMultiple_true() {
ImageChunk imageChunk1 = new ImageChunk("hash1", new Rectangle(0, 0, 100, 100));
List<ImageChunk> images1 = List.of(imageChunk1);
List<ImageChunk> images2 = List.of();

imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, true);

verify(pdfHighlighterMock, times(1)).highlightChunk(any(), any(), any(), eq(BaseColor.RED), eq(0));
}

@Test
public void testCompareImages_insertCase_isMultiple_false() {
ImageChunk imageChunk2 = new ImageChunk("hash2", new Rectangle(0, 0, 100, 100));
List<ImageChunk> images1 = List.of();
List<ImageChunk> images2 = List.of(imageChunk2);

imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, false);

verify(pdfHighlighterMock, times(1)).highlightChunk(any(), any(), any(), eq(BaseColor.GREEN), eq(0));
}

@Test
public void testCompareImages_changeCase_isMultiple_true() {
ImageChunk imageChunk1 = new ImageChunk("hash1", new Rectangle(0, 0, 100, 100));
ImageChunk imageChunk2 = new ImageChunk("hash2", new Rectangle(0, 0, 100, 100));
List<ImageChunk> images1 = List.of(imageChunk1);
List<ImageChunk> images2 = List.of(imageChunk2);

imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, true);

verify(pdfHighlighterMock, times(1)).highlightChunk(any(), any(), any(), eq(BaseColor.RED), eq(0));
verify(pdfHighlighterMock, times(1)).highlightChunk(any(), any(), any(), eq(BaseColor.GREEN), eq(100));
}

@Test
public void testCompareImages_emptyImages1_isMultiple_true() {
List<ImageChunk> images1 = List.of();
List<ImageChunk> images2 = List.of(new ImageChunk("hash2", new Rectangle(0, 0, 100, 100)));

imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, true);

verify(pdfHighlighterMock, times(1)).highlightChunk(any(), any(), any(), eq(BaseColor.GREEN), eq(0));
}

@Test
public void testCompareImages_emptyImages2_isMultiple_true() {
List<ImageChunk> images1 = List.of(new ImageChunk("hash1", new Rectangle(0, 0, 100, 100)));
List<ImageChunk> images2 = List.of();

imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, true);

verify(pdfHighlighterMock, times(1)).highlightChunk(any(), any(), any(), eq(BaseColor.RED), eq(0));
}

@Test
public void testCompareImages_nullImages1_throwsNullPointerException() {
assertThrows(NullPointerException.class, () -> imageComparator.compareImages(pdfContentByteMock, null, List.of(), 100f, true));
}

@Test
public void testCompareImages_nullImages2_throwsNullPointerException() {
assertThrows(NullPointerException.class, () -> imageComparator.compareImages(pdfContentByteMock, List.of(), null, 100f, true));
}

@Test
public void testCompareImages_nullPdfContentByte_throwsNullPointerException() {
assertThrows(NullPointerException.class, () -> imageComparator.compareImages(null, List.of(), List.of(), 100f, true));
}

@Test
public void testCompareImages_nullImages1AndImages2_throwsNullPointerException() {
assertThrows(NullPointerException.class, () -> imageComparator.compareImages(pdfContentByteMock, null, null, 100f, true));
}
