                if (isMultiple) {
                    cb.addTemplate(page1, 0, 0);
                }
            }
            if (pageNumber <= reader2.getNumberOfPages()) {
                page2 = writer.getImportedPage(reader2, pageNumber);
                cb.addTemplate(page2, isMultiple ? width1 : 0, 0);
            }
