                cb.addTemplate(page2, isMultiple ? width1 : 0, 0);
            }
            // Delegate per‑page comparison.
            try {
                pageComparator.comparePage(reader1, reader2, pageNumber, cb, width1, width2, height1, height2, isMultiple);
            } catch (IOException e) {
