import com.example.pdfcompare.util.PDFPageComparator;
import com.itextpdf.text.Document;
import com.itextpdf.text.Rectangle;
import com.itextpdf.text.pdf.PdfReader;
import com.itextpdf.text.pdf.PdfWriter;
import com.itextpdf.text.pdf.PdfContentByte;
import java.io.InputStream;
import java.io.OutputStream;
import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.util.concurrent.atomic.AtomicBoolean;
import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.junit.jupiter.MockitoExtension;
import static org.mockito.Mockito.*;
import static org.junit.jupiter.api.Assertions.*;

@ExtendWith(MockitoExtension.class)
public class PDFComparatorTest {

@Mock
private PDFPageComparator pageComparator;

@InjectMocks
private PDFComparator pdfComparator;

private InputStream pdf1InputStream;
private InputStream pdf2InputStream;
private OutputStream outputStream;
private ByteArrayOutputStream outputStreamMock;

@BeforeEach
public void setUp() throws Exception {
pdf1InputStream = new ByteArrayInputStream("PDF1".getBytes());
pdf2InputStream = new ByteArrayInputStream("PDF2".getBytes());
outputStreamMock = new ByteArrayOutputStream();
outputStream = outputStreamMock;
}

@AfterEach
public void tearDown() throws Exception {
pdf1InputStream.close();
pdf2InputStream.close();
outputStreamMock.close();
}

@Test
public void testComparePDFs_NormalCase_MergesPages() throws Exception {
when(pageComparator.comparePage(any(PdfReader.class), any(PdfReader.class), anyInt(), any(PdfContentByte.class), anyFloat(), anyFloat(), anyFloat(), anyFloat(), anyBoolean()))
.thenReturn(null);

pdfComparator.comparePDFs(pdf1InputStream, pdf2InputStream, outputStream, true);

assertNotNull(outputStreamMock.toByteArray());
}

@Test
public void testComparePDFs_SinglePage_MergesPages() throws Exception {
when(pageComparator.comparePage(any(PdfReader.class), any(PdfReader.class), anyInt(), any(PdfContentByte.class), anyFloat(), anyFloat(), anyFloat(), anyFloat(), anyBoolean()))
.thenReturn(null);

pdfComparator.comparePDFs(pdf1InputStream, pdf2InputStream, outputStream, false);

assertNotNull(outputStreamMock.toByteArray());
}

@Test
public void testComparePDFs_EmptyInputStream_ThrowsIOException() {
pdf1InputStream = new ByteArrayInputStream(new byte[0]);
pdf2InputStream = new ByteArrayInputStream(new byte[0]);

assertThrows(IOException.class, () -> pdfComparator.comparePDFs(pdf1InputStream, pdf2InputStream, outputStream, true));
}

@Test
public void testComparePDFs_NullInputStream_ThrowsIOException() {
pdf1InputStream = null;
pdf2InputStream = new ByteArrayInputStream("PDF2".getBytes());

assertThrows(IOException.class, () -> pdfComparator.comparePDFs(pdf1InputStream, pdf2InputStream, outputStream, true));
}

@Test
public void testComparePDFs_NullOutputStream_ThrowsIOException() {
pdf1InputStream = new ByteArrayInputStream("PDF1".getBytes());
pdf2InputStream = new ByteArrayInputStream("PDF2".getBytes());
outputStream = null;

assertThrows(IOException.class, () -> pdfComparator.comparePDFs(pdf1InputStream, pdf2InputStream, outputStream, true));
}

@Test
public void testComparePDFs_PageSizeMismatch_HandlesCorrectly() throws Exception {
when(pageComparator.comparePage(any(PdfReader.class), any(PdfReader.class), anyInt(), any(PdfContentByte.class), anyFloat(), anyFloat(), anyFloat(), anyFloat(), anyBoolean()))
.thenReturn(null);

pdfComparator.comparePDFs(pdf1InputStream, pdf2InputStream, outputStream, true);

assertNotNull(outputStreamMock.toByteArray());
}

@Test
public void testComparePDFs_PageNotExists_SkipsPage() throws Exception {
when(pageComparator.comparePage(any(PdfReader.class), any(PdfReader.class), anyInt(), any(PdfContentByte.class), anyFloat(), anyFloat(), anyFloat(), anyFloat(), anyBoolean()))
.thenReturn(null);

pdfComparator.comparePDFs(pdf1InputStream, pdf2InputStream, outputStream, true);

assertNotNull(outputStreamMock.toByteArray());
}

@Test
public void testComparePDFs_PageComparatorThrowsException_TranslatesToRuntime() {
when(pageComparator.comparePage(any(PdfReader.class), any(PdfReader.class), anyInt(), any(PdfContentByte.class), anyFloat(), anyFloat(), anyFloat(), anyFloat(), anyBoolean()))
.thenThrow(new IOException("Simulated error"));

assertThrows(RuntimeException.class, () -> pdfComparator.comparePDFs(pdf1InputStream, pdf2InputStream, outputStream, true));
}
}
