private InputStream pdf1InputStream;
private InputStream pdf2InputStream;

@BeforeEach
public void setUp() throws Exception {
outputStream = new ByteArrayOutputStream();
pdf1InputStream = new ByteArrayInputStream("dummy1".getBytes());
pdf2InputStream = new ByteArrayInputStream("dummy2".getBytes());
pdfComparator = new PDFComparator();
