package com.example.pdfcompare.util;

import com.example.pdfcompare.util.ImageComparator;
import com.example.pdfcompare.util.ImageChunk;
import com.itextpdf.text.BaseColor;
import com.itextpdf.text.pdf.PdfContentByte;
import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.mockito.Mock;
import org.mockito.MockitoAnnotations;

import java.util.Arrays;
import java.util.List;

import static org.mockito.Mockito.*;

public class ImageComparatorTest {

@Mock
private PdfContentByte pdfContentByteMock;

private ImageComparator imageComparator;

@BeforeEach
public void setUp() {
MockitoAnnotations.openMocks(this);
imageComparator = new ImageComparator();
}

@AfterEach
public void tearDown() {
imageComparator = null;
}

@Test
public void testCompareImages_normalCase_noDeltas() {
List<ImageChunk> images1 = Arrays.asList(new ImageChunk("id1"), new ImageChunk("id2"));
List<ImageChunk> images2 = Arrays.asList(new ImageChunk("id1"), new ImageChunk("id2"));
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, false);
verify(pdfContentByteMock, never()).rectangle(anyFloat(), anyFloat(), anyFloat(), anyFloat());
}

@Test
public void testCompareImages_insertCase_highlightGreen() {
List<ImageChunk> images1 = Arrays.asList(new ImageChunk("id1"));
List<ImageChunk> images2 = Arrays.asList(new ImageChunk("id1"), new ImageChunk("id2"));
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, false);
verify(pdfContentByteMock, times(1)).rectangle(eq(0f), eq(0f), eq(100f), eq(100f));
}

@Test
public void testCompareImages_deleteCase_highlightRed() {
List<ImageChunk> images1 = Arrays.asList(new ImageChunk("id1"), new ImageChunk("id2"));
List<ImageChunk> images2 = Arrays.asList(new ImageChunk("id1"));
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, false);
verify(pdfContentByteMock, times(1)).rectangle(eq(0f), eq(0f), eq(100f), eq(100f));
}

@Test
public void testCompareImages_changeCase_highlightBoth() {
List<ImageChunk> images1 = Arrays.asList(new ImageChunk("id1"), new ImageChunk("id2"));
List<ImageChunk> images2 = Arrays.asList(new ImageChunk("id3"), new ImageChunk("id2"));
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, false);
verify(pdfContentByteMock, times(2)).rectangle(eq(0f), eq(0f), eq(100f), eq(100f));
}

@Test
public void testCompareImages_multipleTrue_highlightBoth() {
List<ImageChunk> images1 = Arrays.asList(new ImageChunk("id1"), new ImageChunk("id2"));
List<ImageChunk> images2 = Arrays.asList(new ImageChunk("id3"), new ImageChunk("id2"));
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, true);
verify(pdfContentByteMock, times(2)).rectangle(eq(0f), eq(0f), eq(100f), eq(100f));
}

@Test
public void testCompareImages_isMultipleFalse_noXOffset() {
List<ImageChunk> images1 = Arrays.asList(new ImageChunk("id1"));
List<ImageChunk> images2 = Arrays.asList(new ImageChunk("id2"));
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, false);
verify(pdfContentByteMock, times(1)).rectangle(eq(0f), eq(0f), eq(100f), eq(100f));
}

@Test
public void testCompareImages_isMultipleTrue_useWidth1() {
List<ImageChunk> images1 = Arrays.asList(new ImageChunk("id1"));
List<ImageChunk> images2 = Arrays.asList(new ImageChunk("id2"));
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, true);
verify(pdfContentByteMock, times(1)).rectangle(eq(0f), eq(0f), eq(100f), eq(100f));
}

@Test
public void testCompareImages_emptyLists_noDeltas() {
List<ImageChunk> images1 = Arrays.asList();
List<ImageChunk> images2 = Arrays.asList();
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, false);
verify(pdfContentByteMock, never()).rectangle(anyFloat(), anyFloat(), anyFloat(), anyFloat());
}

@Test
public void testCompareImages_oneListEmpty_otherNot_emptyDeltas() {
List<ImageChunk> images1 = Arrays.asList();
List<ImageChunk> images2 = Arrays.asList(new ImageChunk("id1"));
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, false);
verify(pdfContentByteMock, times(1)).rectangle(eq(0f), eq(0f), eq(100f), eq(100f));
}

@Test
public void testCompareImages_sameIds_noDeltas() {
List<ImageChunk> images1 = Arrays.asList(new ImageChunk("id1"), new ImageChunk("id2"));
List<ImageChunk> images2 = Arrays.asList(new ImageChunk("id1"), new ImageChunk("id2"));
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100f, false);
verify(pdfContentByteMock, never()).rectangle(anyFloat(), anyFloat(), anyFloat(), anyFloat());
}

@Test
public void testCompareImages_nullImages1_throwsNullPointerException() {
assertThrows(NullPointerException.class, () -> imageComparator.compareImages(pdfContentByteMock, null, List.of(), 100f, false));
}

@Test
public void testCompareImages_nullImages2_throwsNullPointerException() {
assertThrows(NullPointerException.class, () -> imageComparator.compareImages(pdfContentByteMock, List.of(), null, 100f, false));
}

@Test
public void testCompareImages_nullPdfContentByte_throwsNullPointerException() {
assertThrows(NullPointerException.class, () -> imageComparator.compareImages(null, List.of(), List.of(), 100f, false));
}
}
