package com.example.pdfcompare.util;

import com.example.pdfcompare.util.ImageChunk;
import com.itextpdf.text.pdf.PdfContentByte;
import org.apache.commons.collections4.diff.Diff;
import org.apache.commons.collections4.diff.Delta;
import org.apache.commons.collections4.diff.Patch;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.mockito.Mock;
import org.mockito.MockitoAnnotations;

import java.util.List;

import static org.mockito.Mockito.*;

public class ImageComparatorTest {

@Mock
private PdfContentByte mockPdfContentByte;

private ImageComparator imageComparator;

@BeforeEach
public void setUp() {
MockitoAnnotations.openMocks(this);
imageComparator = new ImageComparator();
}

@Test
public void testCompareImages_ImagesIdentical_NoHighlights() {
List<ImageChunk> images1 = List.of(new ImageChunk("id1"), new ImageChunk("id2"));
List<ImageChunk> images2 = List.of(new ImageChunk("id1"), new ImageChunk("id2"));
float width1 = 100f;
boolean isMultiple = true;

imageComparator.compareImages(mockPdfContentByte, images1, images2, width1, isMultiple);

verifyNoInteractions(mockPdfContentByte);
}

@Test
public void testCompareImages_DeleteAndInsert_HighlightCorrectly() {
List<ImageChunk> images1 = List.of(new ImageChunk("id1"), new ImageChunk("id2"));
List<ImageChunk> images2 = List.of(new ImageChunk("id2"));
float width1 = 100f;
boolean isMultiple = true;

imageComparator.compareImages(mockPdfContentByte, images1, images2, width1, isMultiple);

verify(mockPdfContentByte).rect(anyFloat(), anyFloat(), anyFloat(), anyFloat());
verify(mockPdfContentByte).setRGB(eq((float) 255), eq((float) 0), eq((float) 0));
verify(mockPdfContentByte).setRGB(eq((float) 0), eq((float) 255), eq((float) 0));
}

@Test
public void testCompareImages_ChangeHighlightBoth() {
List<ImageChunk> images1 = List.of(new ImageChunk("id1"), new ImageChunk("id2"));
List<ImageChunk> images2 = List.of(new ImageChunk("id3"), new ImageChunk("id4"));
float width1 = 100f;
boolean isMultiple = true;

imageComparator.compareImages(mockPdfContentByte, images1, images2, width1, isMultiple);

verify(mockPdfContentByte).rect(anyFloat(), anyFloat(), anyFloat(), anyFloat());
verify(mockPdfContentByte).setRGB(eq((float) 255), eq((float) 0), eq((float) 0));
verify(mockPdfContentByte).setRGB(eq((float) 0), eq((float) 255), eq((float) 0));
}

@Test
public void testCompareImages_InsertNoMultiple_HighlightWidth() {
List<ImageChunk> images1 = List.of(new ImageChunk("id1"));
List<ImageChunk> images2 = List.of(new ImageChunk("id2"));
float width1 = 100f;
boolean isMultiple = false;

imageComparator.compareImages(mockPdfContentByte, images1, images2, width1, isMultiple);

verify(mockPdfContentByte).rect(anyFloat(), anyFloat(), anyFloat(), anyFloat());
verify(mockPdfContentByte).setRGB(eq((float) 0), eq((float) 255), eq((float) 0));
}

@Test
public void testCompareImages_EmptyLists_NoHighlights() {
List<ImageChunk> images1 = List.of();
List<ImageChunk> images2 = List.of();

imageComparator.compareImages(mockPdfContentByte, images1, images2, 0, false);

verifyNoInteractions(mockPdfContentByte);
}

@Test
public void testCompareImages_OneEmptyList_DeleteHighlight() {
List<ImageChunk> images1 = List.of(new ImageChunk("id1"));
List<ImageChunk> images2 = List.of();

imageComparator.compareImages(mockPdfContentByte, images1, images2, 0, true);

verify(mockPdfContentByte).rect(anyFloat(), anyFloat(), anyFloat(), anyFloat());
verify(mockPdfContentByte).setRGB(eq((float) 255), eq((float) 0), eq((float) 0));
}

@Test
public void testCompareImages_NullImages1_ExceptionThrown() {
List<ImageChunk> images1 = null;
List<ImageChunk> images2 = List.of(new ImageChunk("id1"));
float width1 = 100f;
boolean isMultiple = true;

assertThrows(NullPointerException.class, () -> imageComparator.compareImages(mockPdfContentByte, images1, images2, width1, isMultiple));
}

@Test
public void testCompareImages_NullImages2_ExceptionThrown() {
List<ImageChunk> images1 = List.of(new ImageChunk("id1"));
List<ImageChunk> images2 = null;
float width1 = 100f;
boolean isMultiple = true;

assertThrows(NullPointerException.class, () -> imageComparator.compareImages(mockPdfContentByte, images1, images2, width1, isMultiple));
}

private void verifyNoInteractions(PdfContentByte mock) {
verify(mock, never()).rect(anyFloat(), anyFloat(), anyFloat(), anyFloat());
verify(mock, never()).setRGB(anyFloat(), anyFloat(), anyFloat());
}
}
