package com.example.pdfcompare.util;

import com.example.pdfcompare.util.ImageChunk;
import com.itextpdf.text.pdf.PdfContentByte;
import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.mockito.Mockito;
import org.mockito.Spy;

import java.util.Arrays;
import java.util.List;
import java.util.stream.Collectors;

import static org.mockito.Mockito.*;

public class ImageComparatorTest {

@Spy
private ImageComparator imageComparator = new ImageComparator();

@Mock
private PdfContentByte pdfContentByteMock;

@Mock
private ImageChunk imageChunk1Mock;

@Mock
private ImageChunk imageChunk2Mock;

@Mock
private ImageChunk imageChunk3Mock;

@Mock
private ImageChunk imageChunk4Mock;

@BeforeEach
public void setUp() {
MockitoAnnotations.openMocks(this);
}

@AfterEach
public void tearDown() {
// Cleanup if needed
}

@Test
void testCompareImagesNoDeltas_ExpectedNoCalls() {
List<ImageChunk> images1 = Arrays.asList(imageChunk1Mock, imageChunk2Mock);
List<ImageChunk> images2 = Arrays.asList(imageChunk1Mock, imageChunk2Mock);
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100.0f, false);
verifyNoInteractions(imageComparator);
}

@Test
void testCompareImagesDeleteDeltas_ExpectedHighlightRed() {
List<ImageChunk> images1 = Arrays.asList(imageChunk1Mock, imageChunk2Mock, imageChunk3Mock);
List<ImageChunk> images2 = Arrays.asList(imageChunk1Mock, imageChunk2Mock);
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100.0f, true);
verify(imageComparator).highlightChunk(any(), images1, pdfContentByteMock, BaseColor.RED, 0);
}

@Test
void testCompareImagesInsertDeltas_ExpectedHighlightGreen() {
List<ImageChunk> images1 = Arrays.asList(imageChunk1Mock, imageChunk2Mock);
List<ImageChunk, ImageChunk> images2 = Arrays.asList(imageChunk1Mock, imageChunk2Mock, imageChunk3Mock);
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100.0f, true);
verify(imageComparator).highlightChunk(any(), images2, pdfContentByteMock, BaseColor.GREEN, 100.0f);
}

@Test
void testCompareImagesChangeDeltas_ExpectedHighlightBoth() {
List<ImageChunk> images1 = Arrays.asList(imageChunk1Mock, imageChunk2Mock);
List<ImageChunk> images2 = Arrays.asList(imageChunk3Mock, imageChunk4Mock);
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100.0f, true);
verify(imageComparator).highlightChunk(any(), images1, pdfContentByteMock, BaseColor.RED, 0);
verify(imageComparator).highlightChunk(any(), images2, pdfContentByteMock, BaseColor.GREEN, 100.0f);
}

@Test
void testCompareImagesIsMultipleFalse_ExpectedInsertXOffsetZero() {
List<ImageChunk> images1 = Arrays.asList(imageChunk1Mock);
List<ImageChunk> images2 = Arrays.asList(imageChunk2Mock);
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100.0f, false);
verify(imageComparator).highlightChunk(any(), images2, pdfContentByteMock, BaseColor.GREEN, 0);
}

@Test
void testCompareImagesEmptyImages1_ExpectedAllDelete() {
List<ImageChunk> images1 = List.of();
List<ImageChunk> images2 = Arrays.asList(imageChunk1Mock, imageChunk2Mock);
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100.0f, true);
verify(imageComparator, times(2)).highlightChunk(any(), images1, pdfContentByteMock, BaseColor.RED, 0);
}

@Test
void testCompareImagesEmptyImages2_ExpectedAllInsert() {
List<ImageChunk> images1 = Arrays.asList(imageChunk1Mock, imageChunk2Mock);
List<ImageChunk> images2 = List.of();
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100.0f, true);
verify(imageComparator, times(2)).highlightChunk(any(), images2, pdfContentByteMock, BaseColor.GREEN, 100.0f);
}

@Test
void testCompareImagesNullImages1_ExpectedNoCalls() {
List<ImageChunk> images1 = null;
List<ImageChunk> images2 = Arrays.asList(imageChunk1Mock, imageChunk2Mock);
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100.0f, true);
verifyNoInteractions(imageComparator);
}

@Test
void testCompareImagesNullImages2_ExpectedNoCalls() {
List<ImageChunk> images1 = Arrays.asList(imageChunk1Mock, imageChunk2Mock);
List<ImageChunk> images2 = null;
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100.0f, true);
verifyNoInteractions(imageComparator);
}

@Test
void testCompareImagesNullImages1AndImages2_ExpectedNoCalls() {
List<ImageChunk> images1 = null;
List<ImageChunk> images2 = null;
imageComparator.compareImages(pdfContentByteMock, images1, images2, 100.0f, true);
verifyNoInteractions(imageComparator);
}
}
