package com.example.pdfcompare.util;

import com.example.pdfcompare.util.ImageComparator;
import com.itextpdf.text.BaseColor;
import com.itextpdf.text.pdf.PdfContentByte;
import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.mockito.Mockito;

import java.util.Arrays;
import java.util.Collections;
import java.util.List;

import static org.junit.jupiter.api.Assertions.*;
import static org.mockito.Mockito.*;

public class ImageComparatorTest {

private ImageComparator imageComparator;
private PdfContentByte mockPdfContentByte;
private List<ImageChunk> mockImages1;
private List<ImageChunk> mockImages2;

@BeforeEach
public void setUp() {
imageComparator = new ImageComparator();
mockPdfContentByte = Mockito.mock(PdfContentByte.class);
mockImages1 = Arrays.asList(new ImageChunk("id1"), new ImageChunk("id2"));
mockImages2 = Arrays.asList(new ImageChunk("id2"), new ImageChunk("id3"));
}

@AfterEach
public void tearDown() {
mockPdfContentByte = null;
mockImages1 = null;
mockImages2 = null;
}

@Test
public void testCompareImages_SameIdentifiers_ExpectHighlightChanges() {
when(mockPdfContentByte.getPageSize()).thenReturn(new Rectangle(0, 0, 100, 100));
imageComparator.compareImages(mockPdfContentByte, mockImages1, mockImages2, 50.0f, true);

verify(mockPdfContentByte, times(2)).rectangle(anyFloat(), anyFloat(), anyFloat(), anyFloat());
verify(mockPdfContentByte, times(2)).setStrokeColor(any(BaseColor.class));
verify(mockPdfContentByte, times(2)).stroke();
}

@Test
public void testCompareImages_DeleteOnly_ExpectHighlightRed() {
mockImages2 = Collections.singletonList(new ImageChunk("id1"));
imageComparator.compareImages(mockPdfContentByte, mockImages1, mockImages2, 50.0f, true);

verify(mockPdfContentByte, times(1)).rectangle(anyFloat(), anyFloat(), anyFloat(), anyFloat());
verify(mockPdfContentByte, times(1)).setStrokeColor(eq(BaseColor.RED));
verify(mockPdfContentByte, times(1)).stroke();
}

@Test
public void testCompareImages_InsertOnly_ExpectHighlightGreen() {
mockImages1 = Collections.singletonList(new ImageChunk("id1"));
imageComparator.compareImages(mockPdfContentByte, mockImages1, mockImages2, 50.0f, false);

verify(mockPdfContentByte, times(1)).rectangle(anyFloat(), anyFloat(), anyFloat(), anyFloat());
verify(mockPdfContentByte, times(1)).setStrokeColor(eq(BaseColor.GREEN));
verify(mockPdfContentByte, times(1)).stroke();
}

@Test
public void testCompareImages_EmptyLists_NoHighlight() {
imageComparator.compareImages(mockPdfContentByte, Collections.emptyList(), Collections.emptyList(), 50.0f, false);

verify(mockPdfContentByte, never()).rectangle(anyFloat(), anyFloat(), anyFloat(), anyFloat());
}

@Test
public void testCompareImages_NullImages_ExpectNullPointerException() {
assertThrows(NullPointerException.class, () -> imageComparator.compareImages(mockPdfContentByte, null, mockImages2, 50.0f, true));
assertThrows(NullPointerException.class, () -> imageComparator.compareImages(mockPdfContentByte, mockImages1, null, 50.0f, false));
}

@Test
public void testCompareImages_NullImages1_ExpectNullPointerException() {
assertThrows(NullPointerException.class, () -> imageComparator.compareImages(mockPdfContentByte, null, mockImages2, 50.0f, true));
}

@Test
public void testCompareImages_NullImages2_ExpectNullPointerException() {
assertThrows(NullPointerException.class, () -> imageComparator.compareImages(mockPdfContentByte, mockImages1, null, 50.0f, false));
}
}
